import React, { useState, useEffect, ChangeEvent } from "react";
import { Button, TextField, CircularProgress, Typography, Paper, Grid } from "@material-ui/core";
import { ArrowRight } from "@material-ui/icons";

const API_KEY = "oWMik3POHlNAyTrd6mVPMIpR20X0Lw1E";

interface Gif {
  id: string;
  images: {
    fixed_width: {
      mp4: string;
    };
  };
}

const GifPicker: React.FC = () => {
  const [query, setQuery] = useState<string>("");
  const [gifs, setGifs] = useState<Gif[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<boolean>(false);
  const [copied, setCopied] = useState<string>("");
  const [limitExceeded, setLimitExceeded] = useState<boolean>(false);
  const [prevResults, setPrevResults] = useState<Gif[]>([]);
  const [suggestions, setSuggestions] = useState<string[]>([]);

  useEffect(() => {
    const fetchRandomGifs = async () => {
      try {
        const response = await fetch(
          `https://api.giphy.com/v1/gifs/trending?api_key=${API_KEY}&limit=3`
        );
        const data = await response.json();

        if (response.status === 429) {
          setLimitExceeded(true);
        } else {
          const gifData = data.data.map((gif: any) => ({
            id: gif.id,
            images: gif.images,
          }));
          setPrevResults(gifData);
        }
      } catch (error) {
        console.error("Error fetching random GIFs:", error);
      }
    };

    fetchRandomGifs();
  }, []);

  const handleInputChange = async (event: ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value;
    setQuery(value);

    try {
      if (value.trim() !== "") {
        const response = await fetch(
          `https://api.giphy.com/v1/gifs/search/tags?api_key=${API_KEY}&q=${encodeURIComponent(
            value
          )}&limit=5`
        );
        const data = await response.json();

        if (response.status === 200) {
          const suggestionsData = data.data.map((suggestion: any) => suggestion.name);
          setSuggestions(suggestionsData);
        }
      } else {
        setSuggestions([]);
      }
    } catch (error) {
      console.error("Error fetching search suggestions:", error);
    }
  };

  const handleSuggestionClick = (suggestion: string) => {
    setQuery(suggestion);
  };

  const searchGifs = async () => {
    setSuggestions([]); // Clear suggestions

    if (query.trim() === "") {
      return;
    }

    setLoading(true);
    setError(false);
    setGifs([]);

    try {
      const response = await fetch(
        `https://api.giphy.com/v1/gifs/search?api_key=${API_KEY}&q=${encodeURIComponent(
          query
        )}&limit=10`
      );
      const data = await response.json();

      if (response.status === 429) {
        setLimitExceeded(true);
      } else {
        const gifData = data.data.map((gif: any) => ({
          id: gif.id,
          images: gif.images,
        }));
        setGifs(gifData);
        setLimitExceeded(false);
      }
    } catch (error) {
      console.error("Error searching GIFs:", error);
      setError(true);
    }

    setLoading(false);
  };

  const copyToClipboard = (url: string) => {
    navigator.clipboard.writeText(url);
    setCopied(url);
    setTimeout(() => {
      setCopied("");
    }, 1500);
  };

  return (
    <div>
      <TextField
        label="Search for GIFs"
        value={query}
        onChange={handleInputChange}
        variant="outlined"
        fullWidth
      />
      {suggestions.length > 0 && (
        <Paper style={{ position: "relative" }}>
          {suggestions.map((suggestion) => (
            <Typography
              key={suggestion}
              variant="body2"
              onClick={() => handleSuggestionClick(suggestion)}
              style={{ display: "flex", alignItems: "center", cursor: "pointer", padding: "8px" }}
            >
              <ArrowRight style={{ marginRight: "8px" }} />
              {suggestion}
            </Typography>
          ))}
        </Paper>
      )}
      <Button variant="contained" color="primary" onClick={searchGifs}>
        Search
      </Button>

      {loading && <CircularProgress />}
      {limitExceeded && (
        <Typography color="error">
          API limit exceeded. Please try again later.
        </Typography>
      )}
      {error && <Typography color="error">Error fetching GIFs.</Typography>}

      <Grid container spacing={2}>
        {gifs.map((gif) => (
          <Grid item xs={4} key={gif.id}>
            <div>
              <video autoPlay loop src={gif.images.fixed_width.mp4}></video>
              <Button onClick={() => copyToClipboard(gif.images.fixed_width.mp4)}>
                {copied === gif.images.fixed_width.mp4 ? "Copied!" : "Copy to Clipboard"}
              </Button>
            </div>
          </Grid>
        ))}
      </Grid>

      <Grid container spacing={2}>
        {prevResults.map((gif) => (
          <Grid item xs={4} key={gif.id}>
            <div>
              <video autoPlay loop src={gif.images.fixed_width.mp4}></video>
              <Button onClick={() => copyToClipboard(gif.images.fixed_width.mp4)}>
                {copied === gif.images.fixed_width.mp4 ? "Copied!" : "Copy to Clipboard"}
              </Button>
            </div>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default GifPicker;
